import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './LoginForm.css'
import user_icon from './Assets/person.jpeg'; 
import password_icon from './Assets/lock.jpeg'; 
import axios from 'axios'



const LoginForm = () => {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  

  const handleSubmit = (event) => {
    event.preventDefault()
    axios.post('http://localhost:3001/', {email, password} )
    .then(result=> {
      console.log(result)
      const msg = result.data
      if(msg === "Not a user" ){
         alert("Not a user") }
      else if(msg === "incorrect password"){
        alert("Incorrect password")}
      else{
         alert("logged in!")}
      })
    .catch(err=> alert(err))
  }

  return (
    <div className='wrapper'>
      <form onSubmit={handleSubmit}>
        <h1>Login</h1>

        <div className="inputbox">
          <input type="email" placeholder='Email' required value={email}
            onChange={(event) => setEmail(event.target.value)}
          />
          <img src={user_icon} alt="" className="icon"/>
        </div>

        <div className="inputbox">
          <input type="password" placeholder='Password' required value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
          <img src={password_icon} alt="" className="icon"/>
        </div>

      <button type="submit" className='button'>Log In</button>
      <p>Dont have an account ? </p>
      <Link to="/signup" >Sign Up</Link>
      
    </form>
    </div>
  )
}

export default LoginForm;
